{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"},"kaggle":{"accelerator":"none","dataSources":[],"isInternetEnabled":true,"language":"python","sourceType":"script","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"import pandas as pd\nimport yfinance as yf\n\nfrom datetime import datetime, timedelta\n\ndef get_return_data(path: str) -> pd.DataFrame:\n    df_ticker_return = pd.read_csv(path)\n    df_ticker_return['Date'] = pd.to_datetime(df_ticker_return['Date'], utc=True)\n    return df_ticker_return\n\ndef get_single_ticker_history(ticker: str,\n                       start: datetime.datetime,\n                       end: datetime.datetime) \n-> pd.DataFrame:\n    ticker_yf = yf.Ticker(ticker)\n    df_return = ticker_yf.history(start = start, end = end+timedelta(days=1))\n    df_return['ticker'] = ticker \n    df_return.reset_index(inplace=True)\n    return df_return\n\ndef get_tickers_history(tickers: list[str],\n                       start: datetime.datetime,\n                       end: datetime.datetime) \n-> pd.DataFrame:\n    tickers_history_dfs = []\n    for ticker in tickers:    \n        df = get_single_ticker_history(ticker, start, end)\n        tickers_history_dfs.append(df)\n    if len(tickers_history_dfs) > 1:\n        return pd.concat(tickers_history_dfs)\n    return df\n\n    \n\n","metadata":{"_uuid":"1fedb142-ac31-4a7c-93a1-f37243bbae6f","_cell_guid":"ec3c4017-066f-4553-bf3e-bed2a19a9f77","trusted":true,"collapsed":false,"jupyter":{"outputs_hidden":false}},"outputs":[],"execution_count":null}]}